(190, 'IGN Games', '200 OK', 728, 'http://www.ign.com/', 'http://corp.ign.com/feeds.html', NULL, 'IGN Games is without a doubt the biggest, best and most visited games site on the Internet. Why? Split into nine channels covering every gaming platform that matters - Sony Playstation 2, Microsoft Xbox, Nintendo GameCube, PC, Nintendo 64, Sony Playstation, Macintosh, Sega Dreamcast, and hand-held machines like Game Boy - IGN Games delivers daily content that tells you everything going on in the world of gaming.', 'Gaming', 'News', NULL, 0, '2003-01-22 00:00:00', 0),
(191, 'Kinetik', 'Connection Failure.  Status code unavailable.', NULL, 'http://www.thekinetik.com', NULL, NULL, 'The game scene discussion board', 'Gaming', 'Forum', 0, 0, '2003-01-22 00:00:00', -1),
(192, 'Binnews', '200 OK', 1078, 'http://www.binnews.com/', NULL, NULL, 'Binnews.com is a site dedicated to the binary newsgroups scene. The staff of binnews tracks what is being posted in the most important binary newsgroups and reports it here. We also share information on the complex subject of the newsgroups and everything affiliated to them.', 'Gaming', 'Bin', NULL, 0, '2003-01-22 00:00:00', 0),
(193, 'Newzbin', '200 OK', 949, 'http://www.newzbin.com/', NULL, NULL, 'Welcome to newzBin, a free site that is dedicated to indexing the contents of usenet/newsgroups. If you do not know what usenet is, or you would like to learn more about newsgroups, you might want to read some tutorials.', 'Gaming', 'Bin', NULL, 0, '2003-01-22 00:00:00', 0),
